import React, { Component } from 'react';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import Course from './CourseComponent';
import Home from './HomeComponent';
import About from './AboutComponent';
import { Switch, Route, Redirect, withRouter } from 'react-router-dom'
import { connect } from 'react-redux';
import { actions } from 'react-redux-form';
import {TEACHERS} from '../shared/teachers';

import { fetchTeachers, fetchCourses} from '../redux/ActionCreators';







const mapStateToProps = state => {
    return {
        courses: state.courses,
        teachers: state.teachers,
    }
}

const mapDispatchToProps = dispatch => ({
    fetchCourses: ()=>{dispatch(fetchCourses())},
    fetchTeachers:()=>{dispatch(fetchTeachers())}

});

class Main extends Component {

    constructor(props) {
        super(props);
    }
    componentDidMount() {
        this.props.fetchCourses();
        this.props.fetchTeachers();
    }

    render() {
        const HomePage = () => {
            return (
                <Home
                    course={this.props.courses.courses.filter((course)=>course.featured)}
                    courseLoading = {this.props.courses.isLoading}
                    courseErrMess={this.props.courses.errMess}
                />
            );
        }

        const about = () => {
            return (
                <About teachers={TEACHERS} />
            );
        }

        return (
            <div>
                <Header />
                        <Switch>
                            <Route path='/home' component={HomePage} />
                            <Route path='/aboutus' component={about} />
                            <Route exact path='/courses' component={() => <Course courses={this.props.courses} />} />
                            <Redirect to='/home' />
                        </Switch>       
                <Footer />
            </div>
        );
    }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Main));
