import React, { Component } from 'react';
import {
  Card, CardBody, CardText, CardImg,
  CardTitle, Breadcrumb, BreadcrumbItem
} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Loading } from './LoadingComponent';


function RenderForm(){

}

function RenderCourse({ course }) {
  if (course != null) {
    return (
      <Card>
        <CardImg width="100%" src={ course.image} alt={course.name} />
        <CardBody>
          <CardTitle>{course.name}</CardTitle>
          <CardText>{course.description}</CardText>
        </CardBody>
      </Card>
    );
  }
  else {
    return (
      <div> </div>
    );
  }
}
function RenderComments({ comments, commentsErrMess,  dishId }) {
  if(commentsErrMess){
    return (
      <div className="container">
        <div className="row">
          <h4>{commentsErrMess}</h4>
        </div>
      </div>
    );
  }
  else if (comments != null) {
    const item = comments.map((comment) => {
      return (
        <li >
          <div className="mt-3">
            {comment.comment}
          </div>
          <div className="mt-3">
            --{comment.author}, {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit' }).format(new Date(Date.parse(comment.date)))}
          </div>
        </li>
      );
    });
    return (
      <div>
        <h4>
          Comments
        </h4>

        <ul className="list-unstyled">
          {item}
        </ul>

      </div>


    )
  }
  else {
    return (
      <div></div>
    )
  }
}
const CourseDetail = (props) => {
  if (props.isLoading) {
    return (
      <div className="container">
        <div className="row">
          <Loading />
        </div>
      </div>
    );
  }
  else if (props.errMess) {
    return (
      <div className="container">
        <div className="row">
          <h4>{props.errMess}</h4>
        </div>
      </div>
    );
  }
  else {
    return (
      <div className="container">
        <div className="row">
          <Breadcrumb>

            <BreadcrumbItem><Link to="/courses">Menu</Link></BreadcrumbItem>
            <BreadcrumbItem active>{props.course.name}</BreadcrumbItem>
          </Breadcrumb>
          <div className="col-12">
            <h3>{props.course.name}</h3>
            <hr />
          </div>
        </div>
        <div className="row">
          <div className="col-12 col-md-5 m-1">
            <RenderCourse course={props.course} />
          </div>
          <div className="col-12 col-md-5 m-1">
            <RenderComments comments={props.comments}
              commentsErrMess = {props.commentsErrMess}
              dishId={props.course.id}
            />
            <RenderForm/>
          </div>
        </div>

      </div>
    )
  }
}






export default CourseDetail;
