import React, { Component } from 'react';
import {
    Card, CardBody, CardText, CardImg,
    CardTitle, Breadcrumb, BreadcrumbItem, Button, Modal, ModalHeader, ModalBody, Form, FormGroup, Input, Label
} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Loading } from './LoadingComponent';



function RenderCourse({ course }) {
    if (course != null) {
        return (
            <Card>
                <CardImg width="100%" src={course.image} alt={course.name} />
                <CardBody>
                    <CardTitle>{course.name}</CardTitle>
                    <CardText>{course.description}</CardText>
                </CardBody>
            </Card>
        );
    }
    else {
        return (
            <div> </div>
        );
    }
}
function RenderComments({ comments, commentsErrMess, dishId }) {
    if (commentsErrMess) {
        return (
            <div className="container">
                <div className="row">
                    <h4>{commentsErrMess}</h4>
                </div>
            </div>
        );
    }
    else if (comments != null) {
        const item = comments.map((comment) => {
            return (
                <li >
                    <div className="mt-3">
                        {comment.comment}
                    </div>
                    <div className="mt-3">
                        --{comment.author}, {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit' }).format(new Date(Date.parse(comment.date)))}
                    </div>
                </li>
            );
        });
        return (
            <div>
                <h4>
                    Comments
        </h4>

                <ul className="list-unstyled">
                    {item}
                </ul>

            </div>


        )
    }
    else {
        return (
            <div></div>
        )
    }
}
class CourseDetail extends Component {

    constructor(props){
        super(props);

        this.state={
            isModalOpen: false
        }
        this.toggleModal = this.toggleModal.bind(this);
        this.handleLogin = this.handleLogin.bind(this);
    }
    toggleModal(){
        this.setState({
            isModalOpen: !this.state.isModalOpen
        });

    }

    handleLogin(event){
        this.toggleModal();
        alert("Username: " + this.username.value + " Password: " + this.password.value
            + " Remember: " + this.remember.checked);
        event.preventDefault();
    }
    render(){
        if (this.props.isLoading) {
            return (
                <div className="container">
                    <div className="row">
                        <Loading />
                    </div>
                </div>
            );
        }
        else if (this.props.errMess) {
            return (
                <div className="container">
                    <div className="row">
                        <h4>{this.props.errMess}</h4>
                    </div>
                </div>
            );
        }
        else {
            return (
                <div className="container">
                    <div className="row">
                        <Breadcrumb>

                            <BreadcrumbItem><Link to="/courses">Courses</Link></BreadcrumbItem>
                            <BreadcrumbItem active>{this.props.course.name}</BreadcrumbItem>
                        </Breadcrumb>
                        <div className="col-12">
                            <h3>{this.props.course.name}</h3>
                            <hr />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-12 col-md-5 m-1">
                            <RenderCourse course={this.props.course} />
                        </div>
                        <div className="col-12 col-md-5 m-1">
                            <RenderComments comments={this.props.comments}
                                commentsErrMess={this.props.commentsErrMess}
                                dishId={props.course.id}
                            />
                        </div>
                        <div>
                            <Button outline onClick={this.toggleModal}><span className="fa fa-sign-in fa-lg"></span> Book a slot</Button>
                            <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                                <ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
                                <ModalBody>
                                    <Form onSubmit={this.handleLogin}>
                                        <FormGroup>
                                            <Label htmlFor="username">Username</Label>
                                            <Input type="text" id="username" name="username"
                                                innerRef={(input) => this.username = input} />
                                        </FormGroup>
                                        <FormGroup>
                                            <Label htmlFor="password">Password</Label>
                                            <Input type="password" id="password" name="password"
                                                innerRef={(input) => this.password = input} />
                                        </FormGroup>
                                        <FormGroup check>
                                            <Label check>
                                                <Input type="checkbox" name="remember"
                                                    innerRef={(input) => this.remember = input} />
                                        Remember me
                                    </Label>
                                        </FormGroup>
                                        <Button type="submit" value="submit" color="primary">Login</Button>
                                    </Form>
                                </ModalBody>
                            </Modal>
                        </div>
                    </div>

                </div>
            )
        }
    }

}






export default CourseDetail;
